INSERT INTO `setor` (`set_cod`, `set_nome`, `set_responsavel`, `set_descricao`, `set_situacao`, `mostrar`) VALUES
(1, 'CONTABILIDADE', 1, 'O setor de contabilidade registra, analisa e interpreta os dados financeiros da empresa para garantir conformidade legal e sucesso nos negócios.', 1, 1),
(2, 'PROCESSOS SELETIVOS E CONCURSO', 2, 'O setor de processos seletivos e concursos é responsável por planejar, organizar e executar todas as etapas do processo de seleção de candidatos para vagas de emprego ou cargos públicos, utilizando critérios claros e objetivos para avaliar as habilidades e competências dos candidatos e garantir a escolha dos mais qualificados.\r\n', 1, 1),
(3, 'SERVIÇO SOCIAL', 3, 'O Serviço Social é responsável por promover o bem-estar social, atuando em áreas como saúde, educação, assistência social e justiça. Os profissionais dessa área realizam atendimento, orientação, acompanhamento e intervenções para garantir direitos e melhorar as condições de vida das pessoas em situação de vulnerabilidade social, utilizando técnicas e estratégias específicas para cada caso.', 1, 1),
(4, 'COMUNICAÇÃO', 4, 'O setor de comunicação é responsável por planejar, desenvolver e executar estratégias de comunicação interna e externa da empresa, utilizando diferentes meios e canais para transmitir informações, ideias e valores da organização. Ele atua na criação de campanhas publicitárias, gerenciamento de redes sociais, produção de conteúdo e relacionamento com a imprensa, visando promover a imagem da empresa, fortalecer a sua marca e estabelecer uma comunicação eficiente com seus públicos.', 1, 1),
(5, 'ENGENHARIA', 6, 'O SETOR DE ENGENHARIA É RESPONSÁVEL POR DESENVOLVER PROJETOS, SOLUÇÕES E INOVAÇÕES PARA ATENDER ÀS NECESSIDADES TÉCNICAS E OPERACIONAIS DA EMPRESA. ELE ABRANGE DIFERENTES ÁREAS DA ENGENHARIA, COMO CIVIL, ELÉTRICA, MECÂNICA, QUÍMICA E DE PRODUÇÃO, E UTILIZA FERRAMENTAS E TECNOLOGIAS AVANÇADAS PARA PROJETAR, CONSTRUIR, MANTER E OTIMIZAR OS PROCESSOS E EQUIPAMENTOS DA EMPRESA. O SETOR DE ENGENHARIA TAMBÉM TRABALHA NA GARANTIA DA SEGURANÇA E QUALIDADE DAS ATIVIDADES DESENVOLVIDAS, ALÉM DE BUSCAR MELHORIAS CONTÍNUAS NOS PROCESSOS E PRODUTOS.', 1, 0),
(6, 'ARQUITETURA', 6, 'O SETOR DE ARQUITETURA É RESPONSÁVEL POR PROJETAR E GERENCIAR A CONSTRUÇÃO DE EDIFICAÇÕES, ESPAÇOS E AMBIENTES QUE ATENDAM ÀS NECESSIDADES E EXPECTATIVAS DA EMPRESA. ELE ATUA NA CONCEPÇÃO DOS PROJETOS, DESDE A ANÁLISE DO ESPAÇO E SUAS POSSIBILIDADES, PASSANDO PELA ELABORAÇÃO DOS DESENHOS E ESPECIFICAÇÕES TÉCNICAS, ATÉ O ACOMPANHAMENTO DA OBRA. O SETOR DE ARQUITETURA TAMBÉM TRABALHA NA BUSCA POR SOLUÇÕES SUSTENTÁVEIS E INOVADORAS PARA OS PROJETOS, VISANDO A HARMONIA COM O MEIO AMBIENTE E A EFICIÊNCIA ENERGÉTICA.', 1, 0),
(7, 'TOPOGRAFIA', 7, 'O SETOR DE TOPOGRAFIA É RESPONSÁVEL POR REALIZAR LEVANTAMENTOS E MEDIÇÕES DE TERRENOS, ÁREAS E CONSTRUÇÕES, UTILIZANDO TÉCNICAS E EQUIPAMENTOS ESPECÍFICOS PARA GARANTIR A PRECISÃO DAS INFORMAÇÕES COLETADAS. ELE ATUA NO MAPEAMENTO DO TERRENO, ELABORAÇÃO DE PLANTAS E MAPAS, ALÉM DE FORNECER INFORMAÇÕES IMPORTANTES PARA A ELABORAÇÃO DE PROJETOS DE ENGENHARIA E ARQUITETURA. O SETOR DE TOPOGRAFIA TAMBÉM TRABALHA NA IDENTIFICAÇÃO E CORREÇÃO DE POSSÍVEIS PROBLEMAS RELACIONADOS AO TERRENO, COMO DESNIVELAMENTOS E IRREGULARIDADES, VISANDO GARANTIR A SEGURANÇA E QUALIDADE DAS CONSTRUÇÕES REALIZADAS.', 1, 0),
(8, 'INFORMÁTICA', 8, 'O setor de informática é responsável por gerenciar a infraestrutura de tecnologia da informação da empresa, incluindo hardware, software, redes e sistemas. Ele atua no desenvolvimento, manutenção e suporte técnico aos sistemas de informação, garantindo a segurança, confiabilidade e disponibilidade das informações utilizadas pelos usuários da empresa.', 1, 1),
(9, 'MOVIMENTO ECONÔMICO', 9, 'O setor de movimento econômico é responsável por gerenciar as atividades financeiras da empresa, como contas a pagar e receber, fluxo de caixa, orçamento e investimentos. Ele atua no controle e análise dos dados financeiros da empresa, buscando identificar oportunidades de redução de custos, aumento de receitas e melhorias nos resultados financeiros. O setor de movimento econômico também trabalha no planejamento financeiro de curto, médio e longo prazo da empresa, visando a sua sustentabilidade e crescimento.', 1, 1),
(72, 'FINANCEIRO', 10, 'FINANCEIRO', 1, 1),
(73, 'CONSORCIO LAMBARI', 1, 'O CONSÓRCIO LAMBARI É CONSTITUÍDO COMO UMA ASSOCIAÇÃO PÚBLICA, COM PERSONALIDADE JURÍDICA DE DIREITO PÚBLICO, SEM FINS LUCRATIVOS, COM AUTONOMIA ADMINISTRATIVA, FINANCEIRA E PATRIMONIAL, REGENDO-SE PELO PRESENTE ESTATUTO, PELA LEI Nº 11.107/2005, PELO DECRETO FEDERAL Nº 6.017/07 E DEMAIS LEGISLAÇÕES PERTINENTES A MATÉRIA. A SEDE DO CONSÓRCIO LAMBARI É NO MUNICÍPIO DE CONCÓRDIA, ESTADO DE SANTA CATARINA, LOCALIZADO À RUA MARECHAL DEODORO, 772, 12º ANDAR – EDIFÍCIO MIRAGE OFFICE – CENTRO.', 1, 1),
(74, 'CISAMAUC', 32, 'CRIADO NO ANO DE 2005 O CONSÓRCIO INTERMUNICIPAL DE SAÚDE DO ALTO URUGUAI CATARINENSE - CIS/AMAUC, VEIO COM O OBJETIVO DE ASSEGURAR A PRESTAÇÃO DE SERVIÇOS DE SAÚDE ESPECIALIZADOS, DE REFERÊNCIA E DE MAIOR COMPLEXIDADE EM NÍVEL AMBULATORIAL PARA A POPULAÇÃO DOS MUNICÍPIOS CONSORCIADOS, DE CONFORMIDADE COM AS DIRETRIZES DO SUS, ASSEGURANDO O ESTABELECIMENTO DE UM SISTEMA DE REFERÊNCIA E CONTRARREFERÊNCIA EFICIENTE E EFICAZ.', 1, 1),
(75, 'ARQ / ENG / TOP', 6, 'RSR', 1, 1);
